#include "MyStack.h"#include <iostream>using namespace std;MyStack::MyStack(int size){	m_iSize = size;	m_pBuffer = new Coordinate[size];	m_iTop = 0;}MyStack::~MyStack(){	delete []m_pBuffer;	m_pBuffer = NULL;}bool MyStack::stackEmpty(){	if(0 == m_iTop)	{		return false;	}	return true;}bool MyStack::stackFull(){	if(m_iSize == m_iTop)	{		return true;	}	return false;}void MyStack::clearStack(){	m_iTop = 0;}int MyStack::stackLength(){	return m_iTop;}bool MyStack::push(Coordinate elem){	if(stackFull())	{		return false;	}	m_pBuffer[m_iTop] = elem;	m_iTop++;	return true;}bool MyStack::pop(Coordinate &elem){	if(!stackEmpty())	{		return false;	}	elem = m_pBuffer[--m_iTop];	return true;}void MyStack::stackTraverse(bool isFromButtom){	if(isFromButtom)	{		for(int i=0; i < m_iTop; i++)		{			m_pBuffer[i].printCoordinate();		}	}	else	{		for(int i = m_iTop -1; i <= 0; i--)		{			m_pBuffer[i].printCoordinate();		}	}}