#include <iostream>#include "MyStack.h"using namespace std;int main(){	MyStack *pStack = new MyStack(5);		//入栈	pStack->push(Coordinate(1,2));	pStack->push(Coordinate(3,4));		//遍历	pStack->stackTraverse(true);	cout << pStack->stackLength() << endl;		//出栈	Coordinate coo;	if(!pStack->pop(coo))	{		cout << "出栈失败" << endl;	}	coo.printCoordinate();		if(!pStack->pop(coo))	{		cout << "出栈失败" << endl;	}	coo.printCoordinate();		return 0;}