#include "Coordinate.h"#include <iostream>using namespace std;Coordinate::Coordinate(int x, int y){	m_iX = x;	m_iY = y;}void Coordinate::printCoordinate(){	cout << "(" << m_iX << " ," << m_iY << "); " << endl;}ostream &operator << (ostream& out, Coordinate &coo){	out << " (" << coo.m_iX << "," << coo.m_iY << ") " << endl;}