#include "arrayTree.h"#include <iostream>using namespace std;int main(){	int node1 = 3;	arrayTree *pTree = new arrayTree(10, &node1);	int node2 = 5;	int node3 = 8;	int node4 = 2;	int node5 = 6;	int node6 = 0;	int node7 = 7;	pTree->AddNode(0, 0, &node2);	pTree->AddNode(0, 1, &node3);	pTree->AddNode(1, 0, &node4);	pTree->AddNode(1, 1, &node5);	pTree->AddNode(2, 0, &node6);	pTree->AddNode(2, 1, &node7);		int* p = pTree->SearchNode(2);	cout << "node=" << *p << endl;		int del = 0;	pTree->DeleteNode(3, &del);	cout << "delete=" << del << endl;	pTree->TreeTraverse();		delete pTree;	pTree = NULL;	return 0;}